mapper.update:
update tb_shop_goods_relation
set
  visibility = #{visibility,jdbcType=TINYINT}

where goods_id = #{goodsId,jdbcType=INTEGER} and shop_id = #{shopId,jdbcType=INTEGER}

mapper.select:
select goods_id
from tb_shop_goods_relation
where shop_id = #{shopID}

SELECT  s.*
FROM tb_shop_goods_relation
AS sg LEFT JOIN tb_shop AS s ON s.id=sg.shop_id where sg.shop_id=#{shopId}
